mapper.select:
 
		select *
		from(
			select /*+INDEX_DESC(SYS_C007525) */
				 rownum rn, post_num, post_title, post_contents, board_num, member_num, post_date
			from post
			where 
	
		

 		
			rownum <= #{cri.pageNum} * #{cri.amount}
		)
		where rn > (#{cri.pageNum} -1) * #{cri.amount}
		and board_num = #{board_num}
